import React from "react";

interface FigureProps {
  imageSrc: string;
  altText: string;
  description: string;
}

const Figure: React.FC<FigureProps> = ({ imageSrc, altText, description }) => {
  return (
    <div className="flex justify-center items-center w-full my-4">
      <figure className="w-3/4 text-center">
        <img
          src={imageSrc}
          alt={altText}
          className="w-full h-auto object-contain mx-auto"
        />
        <figcaption className="mt-2 text-gray-600">{description}</figcaption>
      </figure>
    </div>
  );
};

export default Figure;
