import React from "react";
import {
  Image,
  StyleSheet,
  Text,
  useWindowDimensions,
  View,
} from "react-native";

interface FigureProps {
  imageSrc: any; // Use 'any' for local images or string for remote URLs
  altText: string;
  description: string;
}

const Figure: React.FC<FigureProps> = ({ imageSrc, altText, description }) => {
  const { width } = useWindowDimensions();
  const imageWidth = width * 0.75; // 75% of screen width

  return (
    <View style={styles.container}>
      <View style={[styles.figure, { width: imageWidth }]}>
        <Image
          source={imageSrc}
          style={[styles.image, { width: imageWidth }]}
          resizeMode="contain"
          accessibilityLabel={altText}
        />
        <Text style={styles.description}>{description}</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  figure: {
    alignItems: "center",
  },
  image: {
    aspectRatio: 1, // This will maintain the aspect ratio of the image
    height: undefined, // Height will be calculated based on the aspect ratio
  },
  description: {
    marginTop: 8,
    textAlign: "center",
    color: "#666",
  },
});

export default Figure;
