import React from "react";
import { Link, Route, Routes } from "react-router-dom";

export interface VideoItemProps {
  title: string;
  date: string;
  description: string;
  slug: string;
}

// Define the VideoItem component
export const VideoItem: React.FC<VideoItemProps> = ({
  title,
  date,
  description,
  slug,
}) => (
  <div style={{ marginBottom: "40px" }}>
    <h3 style={{ marginBottom: "10px" }}>{title}</h3>
    <p style={{ marginBottom: "10px", fontStyle: "italic" }}>{date}</p>
    <p>{description}</p>
    <Link to={`/video/${slug}`} style={linkStyle}>
      Watch video
    </Link>
  </div>
);

// Define the VideoList component
const VideoList: React.FC = () => (
  <div style={{ padding: "20px", maxWidth: "800px", margin: "0 auto" }}>
    <h2 style={{ marginBottom: "40px" }}>My Videos</h2>
    <VideoItem
      title="Introduction to Carbon Calc"
      date="October 1, 2024"
      description="A brief overview of our carbon calculator and its features."
      slug="intro-carbon-calc"
    />
    <VideoItem
      title="Coding Journey: From Beginner to Building Carbon Calc"
      date="September 15, 2024"
      description="My personal journey in learning to code and building the Carbon Calc project."
      slug="coding-journey"
    />
  </div>
);

// You can create individual video components similar to your blog posts
const IntroVideo: React.FC = () => (
  <div style={{ padding: "20px", maxWidth: "800px", margin: "0 auto" }}>
    <h2>Introduction to Carbon Calc</h2>
    <p>Video content goes here...</p>
    <Link to="/video" style={linkStyle}>
      Back to Videos
    </Link>
  </div>
);

const CodingJourneyVideo: React.FC = () => (
  <div style={{ padding: "20px", maxWidth: "800px", margin: "0 auto" }}>
    <h2>Coding Journey: From Beginner to Building Carbon Calc</h2>
    <p>Video content goes here...</p>
    <Link to="/video" style={linkStyle}>
      Back to Videos
    </Link>
  </div>
);

// Define the main Video component with routes
const Video: React.FC = () => (
  <Routes>
    <Route path="/" element={<VideoList />} />
    <Route path="/intro-carbon-calc" element={<IntroVideo />} />
    <Route path="/coding-journey" element={<CodingJourneyVideo />} />
  </Routes>
);

const linkStyle: React.CSSProperties = {
  fontSize: "16px",
  color: "blue",
  textDecoration: "underline",
};

export default Video;
