import React from "react";
import { Link, Route, Routes } from "react-router-dom";
import ContinuationOfJourney from "./blog-posts/continuation-of-journey";
import JourneyBegins from "./blog-posts/journey-begins";
import NewPersonalWebsite from "./blog-posts/new-personal-website";
import ProgressUpdate from "./blog-posts/progress-update";
import WelcomeToCarbonCalc from "./blog-posts/welcome-to-carbon-calc";

export interface BlogPostProps {
  title: string;
  date: string;
  excerpt: string;
  slug: string;
}

// Define the BlogPostItem component
export const BlogPostItem: React.FC<BlogPostProps> = ({
  title,
  date,
  excerpt,
  slug,
}) => (
  <div style={{ marginBottom: "40px" }}>
    <h3 style={{ marginBottom: "10px" }}>{title}</h3>
    <p style={{ marginBottom: "10px", fontStyle: "italic" }}>{date}</p>
    <p>{excerpt}</p>
    <Link to={`/blog/${slug}`} style={linkStyle}>
      Read more
    </Link>
  </div>
);

// Define the BlogList component
const BlogList: React.FC = () => (
  <div style={{ padding: "20px", maxWidth: "800px", margin: "0 auto" }}>
    <h2 style={{ marginBottom: "40px" }}>My Blog</h2>
    <BlogPostItem
      title="New Personal Website!"
      date="September 20, 2024"
      excerpt="I made a new website! This is my 5th blog post. I hope you enjoy it."
      slug="new-personal-website"
    />
    <BlogPostItem
      title="Welcome to the Carbon-Calc site!"
      date="January 14, 2024"
      excerpt="Hello and welcome to my fourth blog! Thank you for your time. I hope to outline this new site as well as some updates since my last blog post."
      slug="welcome-to-carbon-calc"
    />
    <BlogPostItem
      title="Continuation of Our Carbon Calculator Journey"
      date="January 8, 2024"
      excerpt="From my first 'Hello World' to building complex web applications..."
      slug="continuation-of-journey"
    />
    <BlogPostItem
      title="Progress Update on the Carbon Calculator"
      date="December 24, 2023"
      excerpt="Hello! This is my second blog post updating our progress on the carbon calculator."
      slug="progress-update"
    />
    <BlogPostItem
      title="Journey Begins with Carbon Calculator"
      date="December 5, 2023"
      excerpt="This blog post is an introduction to my journey in developing a carbon calculator and learning to code."
      slug="journey-begins"
    />
  </div>
);

// Define the main Blog component with routes
const Blog: React.FC = () => (
  <Routes>
    <Route path="/" element={<BlogList />} />
    <Route path="/new-personal-website" element={<NewPersonalWebsite />} />
    <Route path="/welcome-to-carbon-calc" element={<WelcomeToCarbonCalc />} />
    <Route
      path="/continuation-of-journey"
      element={<ContinuationOfJourney />}
    />
    <Route path="/progress-update" element={<ProgressUpdate />} />
    <Route path="/journey-begins" element={<JourneyBegins />} />
  </Routes>
);

const linkStyle: React.CSSProperties = {
  fontSize: "16px",
  color: "#000",
  textDecoration: "underline",
};

export default Blog;
