import React from "react";

interface FigureProps {
  imageSrc: string;
  altText: string;
  caption: string;
  size?: "small" | "medium" | "large" | number;
  title?: string;
}

const Figure: React.FC<FigureProps> = ({
  imageSrc,
  altText,
  caption,
  size = "medium",
  title,
}) => {
  const getImageWidth = () => {
    if (typeof size === "number") {
      return `${size}%`;
    }
    switch (size) {
      case "small":
        return "50%";
      case "large":
        return "100%";
      case "medium":
      default:
        return "80%";
    }
  };

  return (
    <figure style={styles.figureContainer}>
      {title && <h3 style={styles.title}>{title}</h3>}
      <img
        src={imageSrc}
        alt={altText}
        style={{ ...styles.image, maxWidth: getImageWidth() }}
      />
      <figcaption style={styles.caption}>{caption}</figcaption>
    </figure>
  );
};

const styles = {
  figureContainer: {
    marginBottom: "20px",
    display: "flex",
    flexDirection: "column" as const,
    alignItems: "center",
  },
  title: {
    fontSize: "18px",
    fontWeight: "bold" as const,
    marginBottom: "10px",
    textAlign: "center" as const,
  },
  image: {
    height: "auto",
    marginBottom: "10px",
  },
  caption: {
    fontSize: "14px",
    color: "#666",
    textAlign: "center" as const,
  },
};

export default Figure;
