import React from "react";
import { Route, BrowserRouter as Router, Routes } from "react-router-dom";

import CarbonCalc from "./pages/carboncalc";

const App: React.FC = () => {
  const [isProjectsOpen, setIsProjectsOpen] = React.useState(false);

  const styles: Record<string, React.CSSProperties> = {
    header: {
      backgroundColor: "white",
      padding: "20px 10px",
      textAlign: "center" as const,
    },
    title: {
      fontSize: "24px",
      marginBottom: "20px",
    },
    nav: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      flexWrap: "wrap" as const,
      gap: "15px",
    },
    link: {
      color: "black",
      textDecoration: "none",
      fontSize: "16px",
      padding: "5px",
    },
    dropdownContainer: {
      position: "relative" as const,
      display: "inline-block",
    },
    dropdownButton: {
      background: "none",
      border: "none",
      color: "black",
      fontSize: "16px",
      cursor: "pointer",
      padding: "5px",
      fontFamily: '"Times New Roman", Times, serif',
    },
    dropdownContent: {
      display: isProjectsOpen ? "block" : "none",
      position: "absolute" as const,
      backgroundColor: "white",
      minWidth: "140px",
      boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
      zIndex: 1,
      top: "100%",
      left: "50%",
      transform: "translateX(-50%)",
      borderRadius: "4px",
    },
    dropdownLink: {
      color: "black",
      textDecoration: "none",
      padding: "8px 12px",
      display: "block",
      textAlign: "center" as const,
    },
    mainContainer: {
      minHeight: "100vh",
      display: "flex",
      flexDirection: "column" as const,
      fontFamily: '"Times New Roman", Times, serif',
    },
    main: {
      flex: 1,
      padding: "20px",
      display: "flex",
      justifyContent: "center",
      alignItems: "flex-start",
      backgroundColor: "white",
    },
  };

  return (
    <Router>
      <div style={styles.mainContainer}>
        <header style={styles.header}>
          <h1 style={styles.title}>Jack Pearson</h1>
        </header>

        <main style={styles.main}>
          <Routes>
            <Route path="/carboncalc" element={<CarbonCalc />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
};

export default App;
