import React from "react";

interface FigureProps {
  imageSrc: string;
  altText: string;
  caption: string;
  size?: "small" | "medium" | "large" | number;
}

const Figure: React.FC<FigureProps> = ({
  imageSrc,
  altText,
  caption,
  size = "medium",
}) => {
  const getImageWidth = () => {
    if (typeof size === "number") {
      return `${size}%`;
    }
    switch (size) {
      case "small":
        return "50%";
      case "large":
        return "100%";
      case "medium":
      default:
        return "80%";
    }
  };

  return (
    <div style={styles.figureContainer}>
      <img
        src={imageSrc}
        alt={altText}
        style={{ ...styles.image, maxWidth: getImageWidth() }}
      />
      <p style={styles.caption}>{caption}</p>
    </div>
  );
};

const styles = {
  figureContainer: {
    marginBottom: "20px",
    display: "flex",
    flexDirection: "column" as const,
    alignItems: "center",
  },
  image: {
    height: "auto",
    marginBottom: "10px",
  },
  caption: {
    fontSize: "14px",
    color: "#666",
    textAlign: "center" as const,
  },
};

export default Figure;
