import React from "react";
import { Link } from "react-router-dom";
import BlogPost from "./blogposts/BlogPost";
import WritingItem from "./Writing";

const Home: React.FC = () => {
  const latestBlogPost = {
    title: "New Personal Website!",
    date: "September 20, 2024",
    excerpt:
      "I made a new website! This is my 5th blog post. I hope you enjoy it.",
  };

  const latestWritingPiece = {
    title: "Sustainability Consumers as a Catalyst for Global Progress",
    type: "BPhil Thesis",
    description:
      "An in-depth analysis of how sustainable business practices can drive long-term growth and profitability in modern corporations.",
    slug: "thesis-sustainable-practices",
  };

  return (
    <div style={{ padding: "20px", maxWidth: "800px", margin: "0 auto" }}>
      <h2>Home</h2>
      <p>Hi, Welcome to my website!</p>
      <p>
        I made this website to host some of my projects and to improve my web
        development skills.
      </p>

      <h3>Check out the latest</h3>

      <h4>Latest Blog Post</h4>
      <BlogPost
        title={latestBlogPost.title}
        date={latestBlogPost.date}
        excerpt={latestBlogPost.excerpt}
      />
      <Link to="/blog" style={{ display: "block", marginTop: "10px" }}>
        View all blog posts
      </Link>

      <h4>Latest Writing</h4>
      <WritingItem
        title={latestWritingPiece.title}
        type={latestWritingPiece.type}
        description={latestWritingPiece.description}
        slug={latestWritingPiece.slug}
      />
      <Link to="/writing" style={{ display: "block", marginTop: "10px" }}>
        View all writing pieces
      </Link>
    </div>
  );
};

export default Home;
